package bucles;

public class BucleWhile {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//Bucles: Repetir un algoritmo n veces 
		// Variable de control --> Condicion de continuidad
		
		//while --> for
		
		//Programa que muestra todos los números del 1 al 100
		
		int i = 1;
		
		while(i<=100) {
			System.out.print(i + " ");
			i+=1;
			
		}
		System.out.println();
		System.out.println("Valor de la i después del bucle: " + i);
		
		
		
	}

}
